<?php
ini_set('display_errors',1);
error_reporting(-1);
$directory = 'images/';
// current directory
$imgTypes = array('jpeg', 'jpg', 'png', 'gif'); // The extensions of Images that the plugin will read
function getFoldersList ($directory){
    if( !is_dir($directory)){//If the parameter is a directory if not then just return
    return array();
    }
    $results = array();
    $handler = opendir($directory);
    while ($file = readdir($handler)) {
    if ($file !== '.' && $file !== '..' &&   !is_dir($directory.'/'.$file)) {//If it is a folder
    $ctime = filemtime($directory .'/'. $file) . ',' . $file; //BRING THE DATE OF THE FOLDER
    $type = pathinfo($directory, PATHINFO_EXTENSION);
    $data = file_get_contents($directory .'/'. $file);
    $base64 = 'data:image/' . $type . ';base64,' . base64_encode($data);
    $results[strtolower($ctime)] =  $base64;
    }
    }
    return $results;
}

$directory = isset($_GET['path']) ? $_GET['path'] : $directory;
//THE RESULT OF THE JSON CALL
//GET THE CATEGORIES
$imagesInRoot = getFoldersList($directory);
    if(empty($imagesInRoot)):
    exit('data is empty');
else:
